import UIKit

@objc protocol PhoneAndEmailRoutingLogic
{
    func routeToCountrySelection()
    func routeToCreatePin()
}

protocol PhoneAndEmailDataPassing
{
  var dataStore: PhoneAndEmailDataStore? { get }
}

class PhoneAndEmailRouter: NSObject, PhoneAndEmailRoutingLogic, PhoneAndEmailDataPassing
{
  weak var viewController: PhoneAndEmailViewController?
  var dataStore: PhoneAndEmailDataStore?
  
  // MARK: Routing
    
    func routeToCountrySelection() {
        let countryViewController: CountryViewController = viewController?.storyboard?.instantiateViewController(withIdentifier: "CountryViewController") as! CountryViewController
        countryViewController.countrySelectionDelegate = viewController
        countryViewController.defaultCountryName = viewController?.countryName
        countryViewController.defaultCountryCode = viewController?.countryCode
        
        navigateToSomewhere(source: viewController!, destination: countryViewController)
    }
    
    func routeToCreatePin() {
        let pinViewController: PinViewController = viewController?.storyboard?.instantiateViewController(withIdentifier: "PinViewController") as! PinViewController
        if let screenType = viewController?.screenType {
            pinViewController.screenType = screenType
        }
        
        navigateToSomewhere(source: viewController!, destination: pinViewController)
    }

   //MARK: Navigation
  
  func navigateToSomewhere(source: PhoneAndEmailViewController, destination: UIViewController)
  {
    source.show(destination, sender: nil)
  }
  
   //MARK: Passing data
  
  func passDataToSomewhere(source: PhoneAndEmailDataStore, destination: inout UIViewController)
  {

  }
}
